css:
#chat-room {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 100%;
    max-width: 600px;
    margin: 0 auto;
    background-color: #ffcccc;
    border: 2px solid #cc0000;
    border-radius: 10px;
    padding: 20px;
}

#messages {
    flex-grow: 1;
    overflow-y: auto;
    background-color: #ffe6e6;
    border: 1px solid #cc0000;
    border-radius: 5px;
    padding: 10px;
    margin-bottom: 10px;
    height: 300px;
}

#message-input {
    width: calc(100% - 100px);
    padding: 10px;
    border: 1px solid #cc0000;
    border-radius: 5px;
}

#send-button {
    width: 80px;
    padding: 10px;
    background-color: #cc0000;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
}

#send-button:hover {
    background-color: #990000;
}

javascript:
document.getElementById('send-button').addEventListener('click', function() {
    var messageContent = document.getElementById('message-input').value;
    var userEmail = document.getElementById('useremail').textContent;
    if (messageContent.trim() !== '') {
        var messageData = {
            email: userEmail,
            message: messageContent
        };
        $.ajax({
            type: "POST",
            url: "/skibidi",
            data: JSON.stringify(messageData),
            contentType: "application/json",
            success: function(response) {
                console.log("Message sent successfully:", response);
                document.getElementById('message-input').value = '';
            },
            error: function(error) {
                console.error("Error sending message:", error);
            }
        });
    }
});

function displayMessage(email, message) {
    var messagesDiv = document.getElementById('messages');
    var messageElement = document.createElement('div');
    messageElement.textContent = email + ": " + message;
    messageElement.style.padding = '5px';
    messageElement.style.borderBottom = '1px solid #cc0000';
    messagesDiv.appendChild(messageElement);
    messagesDiv.scrollTop = messagesDiv.scrollHeight;
}

multiplayer:
function handleIncomingMessages(message) {
    var parsedMessage = JSON.parse(message);
    displayMessage(parsedMessage.email, parsedMessage.message);
}

