javascript:
var score = 0;
var cursors = 0;
var grandmas = 0;
var cursorCost = 10;
var grandmaCost = 100;

document.getElementById('cookie').addEventListener('click', function() {
    score += 1;
    document.getElementById('score').textContent = score;
    sendScore();
});

document.getElementById('reset-button').addEventListener('click', function() {
    score = 0;
    cursors = 0;
    grandmas = 0;
    cursorCost = 10;
    grandmaCost = 100;
    document.getElementById('score').textContent = score;
});

document.getElementById('cursor-upgrade').addEventListener('click', function() {
    if (score >= cursorCost) {
        score -= cursorCost;
        cursors += 1;
        cursorCost = Math.floor(cursorCost * 1.15);
        document.getElementById('score').textContent = score;
        sendScore();
    }
});

document.getElementById('grandma-upgrade').addEventListener('click', function() {
    if (score >= grandmaCost) {
        score -= grandmaCost;
        grandmas += 1;
        grandmaCost = Math.floor(grandmaCost * 1.15);
        document.getElementById('score').textContent = score;
        sendScore();
    }
});

setInterval(function() {
    score += cursors + grandmas * 5;
    document.getElementById('score').textContent = score;
    sendScore();
}, 1000);

function sendScore() {
    $.ajax({
        type: "POST",
        url: "/skibidi",
        data: JSON.stringify({ score: score, cursors: cursors, grandmas: grandmas }),
        contentType: "application/json",
        success: function(response) {
            console.log("Score sent successfully:", response);
        },
        error: function(error) {
            console.error("Error sending score:", error);
        }
    });
}

multiplayer:
function updateScore(message) {
    if (message && message.score !== undefined) {
        score = message.score;
        cursors = message.cursors || 0;
        grandmas = message.grandmas || 0;
        document.getElementById('score').textContent = score;
    }
}

updateScore(message);
