css:
#game-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100vw;
    height: 100vh;
}

#pacman {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background-color: yellow;
    position: absolute;
}

#map {
    width: 80%;
    height: 80%;
    background-color: #800080;
    position: relative;
    display: flex;
    flex-wrap: wrap;
}

.wall {
    width: 10%;
    height: 10%;
    background-color: #ff0000;
}

javascript:
var pacman = document.getElementById('pacman');
var map = document.getElementById('map');
var pacmanPosition = { x: 0, y: 0 };

function generateMap() {
    for (var i = 0; i < 100; i++) {
        var wall = document.createElement('div');
        wall.className = 'wall';
        map.appendChild(wall);
    }
}

function movePacman(direction) {
    if (direction === 'left') pacmanPosition.x -= 10;
    if (direction === 'right') pacmanPosition.x += 10;
    if (direction === 'up') pacmanPosition.y -= 10;
    if (direction === 'down') pacmanPosition.y += 10;

    pacman.style.transform = 'translate(' + pacmanPosition.x + 'px, ' + pacmanPosition.y + 'px)';
}

document.addEventListener('keydown', function(event) {
    if (event.key === 'ArrowLeft') movePacman('left');
    if (event.key === 'ArrowRight') movePacman('right');
    if (event.key === 'ArrowUp') movePacman('up');
    if (event.key === 'ArrowDown') movePacman('down');
});

generateMap();

multiplayer:
function handleMultiplayer(message) {
    if (message.type === 'move') {
        movePacman(message.direction);
    }
}

$.ajax({
    type: "POST",
    url: "/skibidi",
    data: JSON.stringify({ action: 'join', username: document.getElementById('username').textContent }),
    contentType: "application/json",
    success: response => console.log("Joined game successfully:", response),
    error: error => console.error("Error joining game:", error)
});

handleMultiplayer({ type: 'move', direction: 'right' });

