javascript:
var pacman = document.getElementById('pacman');
var map = document.getElementById('map');
var pacmanPosition = { x: 0, y: 0 };
var gridSize = 40; // Size of each grid square

function generateMap() {
    for (var i = 0; i < 100; i++) {
        var wall = document.createElement('div');
        wall.className = 'wall';
        map.appendChild(wall);

        var dot = document.createElement('div');
        dot.className = 'dot';
        wall.appendChild(dot);
        dot.style.left = '50%';
        dot.style.top = '50%';
        dot.style.transform = 'translate(-50%, -50%)';
    }
}

function movePacman(direction) {
    if (direction === 'left') pacmanPosition.x = Math.max(0, pacmanPosition.x - gridSize);
    if (direction === 'right') pacmanPosition.x = Math.min(map.offsetWidth - gridSize, pacmanPosition.x + gridSize);
    if (direction === 'up') pacmanPosition.y = Math.max(0, pacmanPosition.y - gridSize);
    if (direction === 'down') pacmanPosition.y = Math.min(map.offsetHeight - gridSize, pacmanPosition.y + gridSize);

    pacman.style.transform = 'translate(' + pacmanPosition.x + 'px, ' + pacmanPosition.y + 'px)';
}

document.addEventListener('keydown', function(event) {
    if (event.key === 'ArrowLeft') movePacman('left');
    if (event.key === 'ArrowRight') movePacman('right');
    if (event.key === 'ArrowUp') movePacman('up');
    if (event.key === 'ArrowDown') movePacman('down');
});

generateMap();

