    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 100vh;
    background-color: #ff4d4d;
}

#score-board {
    margin-bottom: 10px;
    font-size: 24px;
    color: #fff;
}

#game-area {
    position: relative;
    width: 80%;
    max-width: 600px;
    height: 600px;
    background-color: #800080;
    overflow: hidden;
}

.snake-segment, .apple {
    position: absolute;
    width: 20px;
    height: 20px;
}

.snake-segment {
    background-color: #ff0000;
}

.apple {
    background-color: #ffff00;
}

javascript:
var gameArea = document.getElementById('game-area');
var scoreDisplay = document.getElementById('score');
var snake = [{x: 200, y: 200}];
var apple = {x: 100, y: 100};
var direction = {x: 20, y: 0};
var score = 0;

function createSegment(segment) {
    var segmentDiv = document.createElement('div');
    segmentDiv.style.left = segment.x + 'px';
    segmentDiv.style.top = segment.y + 'px';
    segmentDiv.classList.add('snake-segment');
    gameArea.appendChild(segmentDiv);
}

function createApple() {
    var appleDiv = document.createElement('div');
    appleDiv.style.left = apple.x + 'px';
    appleDiv.style.top = apple.y + 'px';
    appleDiv.classList.add('apple');
    gameArea.appendChild(appleDiv);
}

function moveSnake() {
    var head = {x: snake[0].x + direction.x, y: snake[0].y + direction.y};
    snake.unshift(head);
    if (head.x === apple.x && head.y === apple.y) {
        score += 10;
        scoreDisplay.textContent = 'Score: ' + score;
        placeApple();
    } else {
        snake.pop();
    }
}

function placeApple() {
    apple.x = Math.floor(Math.random() * (gameArea.clientWidth / 20)) * 20;
    apple.y = Math.floor(Math.random() * (gameArea.clientHeight / 20)) * 20;
    var appleDiv = document.querySelector('.apple');
    appleDiv.style.left = apple.x + 'px';
    appleDiv.style.top = apple.y + 'px';
}

function updateGame() {
    gameArea.innerHTML = '';
    snake.forEach(createSegment);
    createApple();
    moveSnake();
    if (isGameOver()) {
        alert('Game Over! Your score: ' + score);
        resetGame();
    } else {
        setTimeout(updateGame, 200);
    }
}

function isGameOver() {
    var head = snake[0];
    return head.x < 0 || head.x >= gameArea.clientWidth || head.y < 0 || head.y >= gameArea.clientHeight || snake.slice(1).some(segment => segment.x === head.x && segment.y === head.y);
}

function resetGame() {
    snake = [{x: 200, y: 200}];
    direction = {x: 20, y: 0};
    score = 0;
    scoreDisplay.textContent = 'Score: 0';
    placeApple();
    updateGame();
}

document.addEventListener('keydown', function(event) {
    if (event.key === 'ArrowUp' && direction.y === 0) direction = {x: 0, y: -20};
    if (event.key === 'ArrowDown' && direction.y === 0) direction = {x: 0, y: 20};
    if (event.key === 'ArrowLeft' && direction.x === 0) direction = {x: -20, y: 0};
    if (event.key === 'ArrowRight' && direction.x === 0) direction = {x: 20, y: 0};
});

updateGame();

