function sendGameState() {
    var gameState = {
        snake: snake,
        apple: apple,
        score: score
    };
    $.ajax({
        type: "POST",
        url: "/skibidi",
        data: JSON.stringify(gameState),
        contentType: "application/json",
        success: response => console.log("Game state sent successfully:", response),
        error: error => console.error("Error sending game state:", error)
    });
}

function receiveGameState(message) {
    var receivedState = JSON.parse(message);
