    if (!gameRunning) return;
    gameArea.innerHTML = '';
    snake.forEach(createSegment);
    createApple();
    moveSnake();
    if (isGameOver()) {
        gameRunning = false;
        restartButton.style.display = 'block';
    } else {
        setTimeout(updateGame, 200);
    }
}

function isGameOver() {
    var head = snake[0];
    return head.x < 0 || head.x >= gameArea.clientWidth || head.y < 0 || head.y >= gameArea.clientHeight || snake.slice(1).some(segment => segment.x === head.x && segment.y === head.y);
}

function resetGame() {
    snake = [{x: 200, y: 200}];
    direction = {x: 20, y: 0};
    score = 0;
    scoreDisplay.textContent = 'Score: 0';
    placeApple();
    gameRunning = true;
    restartButton.style.display = 'none';
    updateGame();
}

document.addEventListener('keydown', function(event) {
    if (event.key === 'ArrowUp' && direction.y === 0) direction = {x: 0, y: -20};
    if (event.key === 'ArrowDown' && direction.y === 0) direction = {x: 0, y: 20};
    if (event.key === 'ArrowLeft' && direction.x === 0) direction = {x: -20, y: 0};
    if (event.key === 'ArrowRight' && direction.x === 0) direction = {x: 20, y: 0};
});

restartButton.addEventListener('click', resetGame);

updateGame();

