css:
#about-page {
  margin-top:55px;padding: 40px;



}

h1 {
  text-align: center;
  font-size: 2rem;
  color: #004080;
  margin-bottom: 20px;
}

.author-container {
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  justify-content: center;
}

.author {
  border: 2px solid #004080;
  padding: 20px;
  background-color: #ffffff;
  text-align: center;
  max-width: 300px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.author h2 {
  font-size: 1.5rem;
  color: #004080;
  margin: 10px 0;
}

.author p {
  font-size: 1rem;
  color: #333333;
  line-height: 1.6;
}

.author-image {
  width: 150px;
  height: 150px;
  object-fit: cover;
  border-radius: 50%;
  border: 2px solid #004080;
  margin-bottom: 10px;
}
